import { State } from '../reducers'
import { isNil, sort } from 'ramda'
import { ProductState } from '../reducers/Product'


export function selectCheapestAndRecommendedOffer(state: State) {
    const offers = state.product.similarProducts

    if (isNil(offers) || offers.length === 0) {
        return {}
    }

    console.log(offers)

    return {
        cheapest: sort((first: { price: number }, second: { price: number }) => first.price - second.price, offers)[0],
        recommended: sort((first: { score: number }, second: { score: number }) => second.score - first.score, offers)[0]
    }
}
