import React, { useState } from 'react'
import cn from 'classnames'

import './CompanyGroup.css'
import Stars from '../../../common/Starts'


export default function CompanyGroup({title}: {title: string}) {
    const [ isActive, setIsActive ] = useState(false)
    const betterPrice = 1200
    const list = [
        {title: `ООО "Cварка мастер"`, ration: 2, price: 1200, location: 'Москва'},
        {title: `ООО "Cварка мастер"`, ration: 2, price: 1200, location: 'Москва'},
    ]
    return (
        <div className={ cn('company-group', isActive && 'company-group_active') }>
            <div className="company-group__title">
                {title}
                {betterPrice > 0 && (
                    <span className="company-group__better-offer">
                        лучшее предложение
                        <span className="company-group__better-price">{betterPrice} ₽</span>
                    </span>) }
            </div>
            { isActive && (
                <div className="company-group__list">
                    {list.map((company) => (
                        <Company company={company} key={title}/>
                    ))}
                </div>
            )}
            <div className="company-group__spoiler" onClick={ () => setIsActive(!isActive) }>
                {isActive ? 'Меньше' : 'Больше'}
            </div>
        </div>
    )
}

interface CompanyProps {
    title: string
    ration: number
    price: number
    location: string
}

function Company({company}: {company: CompanyProps}) {
    const {title, location, price, ration} = company
    return (
        <div className="company">
            <div className="offer__name">{title}</div>
            <Stars ration={ration}/>
            <div className="company__price">{price}</div>
            <div className="company__location">{location}</div>
            <div className="company__location">Надёжный контрагент</div>
        </div>
    )
}
