import React from 'react'

import ElonMusk from '../../images/elonMusk.jpg'
import './Offers.css'
import { useSelector } from 'react-redux'
import { selectCheapestAndRecommendedOffer } from '../../selectors/product'
import { State } from '../../reducers'
import { isNil } from 'ramda'

function Offers() {
    const { cheapest, recommended } = useSelector<State, any>(selectCheapestAndRecommendedOffer)
    // const { avatarSrc, name, type, features, description } = { avatarSrc: ElonMusk, name: 'ИП Илья', type: 'услуга', features: [ '2 клиента' ], description: 'Сотрудник ООО "Завод имени Ильича". Слесарь 3 разряда'  }
    // const recommendedOffer = { avatarSrc: ElonMusk, name: `ООО "Пол - это лава"`, type: 'услуга', features: ['4 клиента', 'Без риска', 'Надежный'], description: 'Сотрудник ООО "Завод имени Ильича". Слесарь 3 разряда'  }

    if (isNil(cheapest) || isNil(recommended)) {
        return
    }

    return (
        <div className="offers">
            <div className="offer">
                <div className="offer__title">БОЛЕЕ ДЕШЁВЫЕ ПРЕДЛОЖЕНИЯ</div>
                <Offer
                    description={ cheapest.description }
                    avatarSrc={ cheapest.imageSrc }
                    name={ cheapest.name }
                    type={ cheapest.type }
                    features={ cheapest.features }
                />
            </div>
            <div className="offer">
                <div className="offer__title">РЕКОМЕНДУЕМЫЕ ПРЕДЛОЖЕНИЯ</div>
                <Offer
                    description={ recommended.description }
                    avatarSrc={ recommended.imageSrc }
                    name={ recommended.name }
                    type={ recommended.type }
                    features={ recommended.features }
                />
            </div>
        </div>
    )
}

interface OfferProps {
    name: string
    type: string
    features: string[]
    avatarSrc: string
    description: string
}

function Offer(props: OfferProps) {
    const {name, type, features, avatarSrc, description} = props
    return (
        <div className="offer__content">
            <div className="offer__user">
                <img src={ avatarSrc } className="offer__icon" />
                {/*<div className="offer__icon"/>*/}
                <div className="offer__user-content">
                    <div className="offer__name">{name}</div>
                    <div className="offer__type">{type}</div>
                </div>
            </div>
            <div className="offer__work">
                { description }
            </div>
            <div className="offer__feature">
                <div className="offer__feature-title">Особенности</div>
                <div className="offer__feature-options">
                    { features.map((feature) =>
                        <div>{feature}</div>
                    )}
                </div>
            </div>
        </div>
    )
}


export default Offers
