import { call, fork, cancel, delay } from 'redux-saga/effects'

import * as api from '../api'
import * as actions from '../actions'
import { assertResponse } from '../errorHandling'


export function * productInitialized({ payload: positionId }: any) {
    try {
        const response = yield call(api.getProduct, { positionId })

        assertResponse(response)

        const similarProducts = yield call(api.getSimilarProductOffers,  { positionId, currentOrganizationId: response.organizationId })

        assertResponse(similarProducts)

        yield call(actions.productInitialized.success, { ...response, similarProducts: similarProducts.positions })
    } catch ({ message }) {
        yield call(actions.errorHandle.occur, message)
    } finally {
    }
}
