import React, { useState } from 'react'
import cn from 'classnames'
import {min, reduce} from 'ramda'

import './CompanyGroup.css'
import Stars from '../../../common/Starts'
import { numberWithSpaces } from '../../../utils'
import {OrganizationType, TrustLevel} from '../../../dto/Product'


export default function CompanyGroup({title, type: companyType}: {title: string, type: OrganizationType}) {
    const [ isActive, setIsActive ] = useState(false)
    const price = 35000
    const list = [
        {title: `ООО "Cварка мастер"`, score: 2, price: 35000, location: 'Москва', type: OrganizationType.ORG},
        {title: `ООО "Молдова Svarka"`, score: 5, price: 60000, location: 'Москва', type: OrganizationType.ORG},
        {title: `ООО "Сварка экспресс"`, score: 1, price: 33000, location: 'Владимир', type: OrganizationType.ORG},
        {title: `ИП Иванова`, score: 2, price: 89000, location: 'Москва', type: OrganizationType.IP},
        {title: `Красавица`, score: 5, price: 77000, location: 'Санкт-петербург', type: OrganizationType.SAM},
    ]
    const minPrice : any = (type: OrganizationType) => reduce(min, Infinity, list.filter((c) => c.type === type).map((i) => i.price))
    return (
        <div className={ cn('company-group', isActive && 'company-group_active') }>
            <div className="company-group__title">
                {title}
                {price > minPrice(companyType) && (
                    <span className="company-group__better-offer">
                        лучшее предложение
                        <span className="company-group__better-price">{numberWithSpaces(minPrice(companyType))} ₽</span>
                    </span>) }
            </div>
            <div className={cn("company-group__list", isActive && 'company-group__list_active')}>
                {list.filter((c) => c.type === companyType).map((company) => (
                    <Company company={company} key={title}/>
                ))}
            </div>
            <div className="company-group__spoiler" onClick={ () => setIsActive(!isActive) }>
                {isActive ? 'Меньше' : 'Больше'}
            </div>
        </div>
    )
}

interface CompanyProps {
    title: string
    score: number
    price: number
    location: string
}

function Company({company}: {company: CompanyProps}) {
    const {title, location, price, score} = company
    return (
        <div className="company">
            <div className="offer__name">{title}</div>
            <Stars score={score}/>
            <div className="company__price">{numberWithSpaces(price)}</div>
            <div className="company__location">{location}</div>
            <div className="company__location">{ TrustLevel.HIGH }</div>
        </div>
    )
}
